sys_variable_value.value:
(function execute(inputs, outputs) {
if(inputs.status_code == "200"){
  var response = JSON.parse(inputs.response);
outputs.page_id = response.id;
  outputs.contents = inputs.response;
}
})(inputs, outputs);

