sys_variable_value.value:
(function execute(inputs, outputs) {
  var requestbody = {};

  requestbody.type = "page";

  var json = {};
  json.key = "SERVICENOW";
  requestbody.space = json;
  var body = {};
  body.storage = {};
  if (inputs.create_test_report == "true") {
   requestbody.title = "Test Execution Report - " + inputs.title;
    var ancestor = [];
    ancestor.push({
      "id": inputs.parent_id
    });
    requestbody.ancestors = ancestor;
    body.storage.value = new ConfluenceUtil().buildtestreport(inputs);
  } else {
    requestbody.title = inputs.title;
    body.storage.value = new ConfluenceUtil().buildskeleton(inputs);
  }
  body.storage.representation = "storage";
  requestbody.body = body;


  outputs.json = JSON.stringify(requestbody);
})(inputs, outputs);

