sys_script_include.script:
var ConfluenceSetStructure = Class.create();
ConfluenceSetStructure.prototype = {
    initialize: function() {},
    setDescription: function(inputs) {
        var test = inputs.description;
        var descr = test.substring(test.indexOf("<p>") + 4, test.indexOf("</p>"));
        return descr;
    },

    setErrorMessage: function(inputs) {
        var test = inputs.description;
        var start = test.indexOf("<p><b>Error Messages:</b>");
        var rest = test.substring(start);
        rest = rest.substring(0, rest.indexOf("</p>"));
        start = rest.indexOf("<li>");
        rest = rest.substring(start);
        rest = rest.replace(/<li>/g, "");
        rest = rest.replace("</ul>", "");
        var err_arr = rest.split("</li>");
        for (var str in err_arr) {
            err_arr[str] = (err_arr[str]).trim();
            err_arr[str] = "<tr><td><p><span style=\"color: rgb(0,0,0);\">" + err_arr[str] + "</span></p></td></tr>";
        }
        return err_arr.join(' ');
    },
    createInputOuputHTML: function(test_array) {
        var test_html_concat = "";
        for (var item in test_array) {
            test_html_concat = test_html_concat + "<p><strong>" + test_array[item] + " : </strong><br /> </p> ";
        }
		return test_html_concat;
    },
    createtestcase: function(inputs) {
        var test_title = "Title";
        var test_input = ["input1", "input2", "input3"];
        var test_output = ["output1", "output2", "Status", "Error Message"];

        var casetable = "<table data-layout=\"default\"><colgroup><col style=\"width: 151.0px;\" /><col style=\"width: 211.0px;\" /><col style=\"width: 211.0px;\" /><col style=\"width: 115.0px;\" /><col style=\"width: 71.0px;\" /></colgroup><tbody><tr><th><p>Test Case - Description</p></th><th><p>Inputs</p></th><th><p>Outputs</p></th><th><p>Expected Result?</p></th><th><p>Notes</p></th></tr><tr><td><p>" + test_title + "</p></td><td>" + new ConfluenceSetStructure().createInputOuputHTML(test_input) + "</td><td><p> " + new ConfluenceSetStructure().createInputOuputHTML(test_output) + "</p></td><td><p>Snow instance check:</p><ul><li><p>&nbsp;link fetched.</p></li></ul></td><td><p><br /></p></td></tr></tbody></table>";


        var generictable = "<h4>TCI1</h4><table data-layout=\"default\"><colgroup><col style=\"width: 151.0px;\" /><col style=\"width: 218.0px;\" /><col style=\"width: 208.0px;\" /><col style=\"width: 125.0px;\" /><col style=\"width: 57.0px;\" /></colgroup><tbody><tr><th><p>Test Case - Description</p></th><th><p>Inputs</p></th><th><p>Outputs</p></th><th><p>Expected Result?</p></th><th><p>Notes</p></th></tr><tr><td><p>The generated session token is not valid.</p></td><td>" + new ConfluenceSetStructure().createInputOuputHTML(test_input) + "</td><td><p>" + new ConfluenceSetStructure().createInputOuputHTML(test_output) + "</p></td><td><p><span style=\"color: rgb(255,255,255);\">YES</span></p><p>Snow instance check:</p><ul><li><p>&nbsp;link fetched.</p></li></ul></td><td><p><br /></p></td></tr></tbody></table><p>TCI2</p><table data-layout=\"default\"><colgroup><col style=\"width: 151.0px;\" /><col style=\"width: 225.0px;\" /><col style=\"width: 210.0px;\" /><col style=\"width: 112.0px;\" /><col style=\"width: 61.0px;\" /></colgroup><tbody><tr><th><p>Test Case - Description</p></th><th><p>Inputs</p></th><th><p>Outputs</p></th><th><p>Expected Result?</p></th><th><p>Notes</p></th></tr><tr><td><p>Credentials not associated with appropriate permission.</p></td><td>" + new ConfluenceSetStructure().createInputOuputHTML(test_input) + " </td><td>" + new ConfluenceSetStructure().createInputOuputHTML(test_output) + "</td><td><p><span style=\"color: rgb(255,É•,255);\">YES</span></p><p>Snow instance check:</p><ul><li><p>&nbsp;</p></li></ul></td><td><p><br /></p></td></tr></tbody></table>";
        var concattable = "";
        for (var item = 0; item < inputs.no_of_test_cases; item++) {
            concattable = concattable + "<p>TC" + (parseInt(item) + 1) + "</p>";
            concattable = concattable.concat(casetable);
        }

        concattable = concattable.concat(generictable);
        return concattable;

    },
    type: 'ConfluenceSetStructure'
};
